mapper.select:
select CheckListCode
from quality_check
where Qulity_CheckID=#{0}

mapper.update:
update quality_check
IsPush='已推送', Progress='录入中'
where Qulity_CheckID=#{0}

update quality_check
Issued='已下达', Progress='整改中'
where Qulity_CheckID=#{0}

update quality_check
IsPush='通过', Progress='完成'
where Qulity_CheckID=#{0}

update quality_check

    IsPush='通过',
    Progress='完成',
    FinishDate=#{finishDate}

where Qulity_CheckID=#{qualityCheckID}

update quality_check
Issued='已整改', Progress='审查中'
where Qulity_CheckID=#{0}
